var link=document.querySelector(".btn-write-us"),popup=document.querySelector(".modal-window"),close=popup.querySelector(".modal-window-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),letter=popup.querySelector("[name=letter]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-window-show")}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-window-show")}),form.addEventListener("submit",function(e){login.value&&email.value&&letter.value||(e.preventDefault(),console.log("Необходимо заполнить все поля"))});
